operation.expression:
let $logging_level := switch ($temp.tmp_search_logging_level) case "ERROR" return <condition>LOGGING_LEVEL = 3</condition> case "WARNING" return <condition>LOGGING_LEVEL &gt;= 2</condition> case "INFO" return <condition>LOGGING_LEVEL &gt;= 1</condition> default return () let $query :=<query> <condition>{$temp.tmp_search_field} LIKE '%{$temp.tmp_search_string}%'</condition> <condition>PROCESS_NAME LIKE '%{$temp.tmp_search_process}%'</condition> <condition>EVENT_TIME &gt;= {$temp.tmp_time_from}</condition> <condition>EVENT_TIME &lt;= {$temp.tmp_time_to}</condition> {$logging_level} </query>
let $conditions := for $condition in $query/condition/text() where (:Filter conditions that had empty values:) not (contains($condition,"''")) and not(contains($condition,"%%")) return $condition
let $limit_part := if (empty($temp.tmp_evt_query_limit)) then () else concat("LIMIT ",$temp.tmp_evt_query_limit)
return concat(string-join($conditions, "&#10; AND ")," ",$limit_part)
