restSimpleBinding.url:
{(:GET /gists/public:)
let $uriParts := ($base_uri,"gists/public")

let $p_since 	:= if (empty($since) ) then () else concat("since=",encode-for-uri($since ))
let $p_per_page := if (empty($per_page )) then () else concat("per_page=",encode-for-uri($per_page ))

(: action query  parameters:)
let $paramList := ($p_since, $p_per_page)

return
string-join((string-join($uriParts,"/"),string-join($paramList, "&amp;")),"?")}

field.xquery:
{let $payload := $RESTResponse/*:payload/text()
let $gist_list := util:parseJSON($payload)
return 
for $item in $gist_list
return
<gist>
 {$item/*}
</gist>}

